def main(args):
    for referee in REFEREES:
        created_referee = Referee.objects.create(
            referee_id=referee[0],
            name_common=referee[1],
            name_given=referee[2],
            name_family=referee[3],
            suffix=referee[4],
            name_sort=referee[5],
            name_alt=referee[6],
            is_active=True if referee[7] == 1 else False,
            is_active_onfield=True if referee[8] == 1 else False
        )
        print(created_referee.id)


if __name__ == '__main__':
    
    import argparse

    parser = argparse.ArgumentParser(
        description="A script to backfill MLS referees based on Jay's SQL dump."
    )

    parser.add_argument('--commit', required=False, action='store_true')

    args = parser.parse_args()

    with transaction.atomic():
        main(args)
        if not args.commit:
            raise DryRunException()
